  //       )
  //     );
  //   } else {
  //     setCartItems([...cartItems, { ...product, qty: 1 }]);
  //   }
  // };

  return (
    <CartProvider>
      <Header />
      <Switch>
        <Route path="/" exact>
          <Home products={products} />
        </Route>
        <Route path="/home">
          <Home products={products} />
        </Route>
        <Route path="/about" component={About} />
        <Route path="/shop" component={Shop} />
        <Route path="/help" component={Help} />
        <Route path="/Cart">
          <Cart  />
        </Route>
      </Switch>
      <Footer />
    </CartProvider>
  );
}

export default App;
