const Cart = (props) => {
  const { cartItems, onAdd, onRemove } = props;
  return (
    <div className="cart">
      <div>
        {/* <div>{cartItems.length === 0 && <div>cart is empty.</div>}</div> */}
        {cartItems.map((items) => (
          <div className="row" key={items.id}>
            <div>{items.name}</div>
            <div>
              <button className="btn-remove" onClick={() => onRemove(items)}>
                -
              </button>
              <button className="btn-add" onClick={() => onAdd(items)}>
                +
              </button>
            </div>
            <div>{items.qty} x {items.price}</div>
          </div>
        ))}
      </div>
    </div>
  );
};
export default Cart;
