  <div class="container-fluid">
    
    <button class="navbar-toggler" type="button" data-bs-toggle="collapse" data-bs-target="#navbarNavAltMarkup" aria-controls="navbarNavAltMarkup" aria-expanded="false" aria-label="Toggle navigation">
      <span class="navbar-toggler-icon"></span>
    </button>
    <div class="collapse navbar-collapse" id="navbarNavAltMarkup">
      <div class="navbar-nav">
        <NavLink to="/home" class="nav-link active" aria-current="page" href="#">Home</NavLink>
        <NavLink to="/about" class="nav-link" href="#">About</NavLink>
        <NavLink to="/shop" class="nav-link" href="#">Shop</NavLink>
        <NavLink to="/help" class="nav-link ">Help</NavLink>
        <NavLink to="/help" class="nav-link ">Help</NavLink>
      </div>
    </div>
  </div>
</nav>
  );
};
const mapStateToProps = (state) => {
  return {
    cart: state.shop.cart,
  };
};

export default connect(mapStateToProps)(Header);
