import React from "react";
import CartItem from "./CartItem";

const ShoppingCart = ({cart}) => {
  
  return (
    <React.Fragment>
      <h4>Shopping Cart</h4>
      <div className="border-small"></div>
      <div className="cart-item">
        {cart.map((item) => (
          <CartItem key={item.id}
            itemData={item}
          />))
        }
      </div>
    </React.Fragment>
  );
};

export default ShoppingCart;
