import CartContext from "./Cart-context";
import { useReducer } from "react";

const defaultCartState = {
  items: [],//item array
  totalAmount: 0,//Total amount of items in cart
};

const cartReducer=(state,action)=>{
  //To add item in cart trigger action ADD
  if(action.type==='ADD'){
    const updatedItems= state.items.conact(action.item);//to get updated items
    const updatedTotalAmount=state.totalAmount + action.item.price * action.item.qty;
  }
  return defaultCartState;
};

const CartProvider = (props) => {
  const [cartState, cartAction] = useReducer(cartReducer, defaultCartState);
  const addItemToCartHandler = (item) => {
    cartAction({ type: "ADD", item: item });
  };
  const removeItemFromCartHandler = (id) => {
    cartAction({ type: "REMOVE", id: id });
  };

  const cartContext = {
    items: cartState.items,
    totalQty: cartState.totalAmount,
    addItem: addItemToCartHandler,
    removeItem: removeItemFromCartHandler,
  };
  return (
    <CartContext.Provider value={cartContext}>
      {props.children}
    </CartContext.Provider>
  );
};

export default CartProvider;
