import Card from "../../UI/Card";

const CartItem = (props) => {
  const { items=[] } = props;
  return (
    <Card>
      <div className="row" key={items.id}>
        <div className="col display-img col-3">
          <img src={items.image} className="small-prod-img" alt={items.name} />
        </div>
        <div className="col col-6">
          <div>{items.name}</div>
          <div>Rs {items.price}</div>
        </div>
        <div className="col col-3">
          <button type="button" className="btn-addItem">
            +
          </button>
          <input type="number" step="1" min="1" max="5" value={items.qty} />
          <button type="button" className="btn-removeItem">
            -
          </button>
        </div>
      </div>
    </Card>
  );
};
export default CartItem;
