import { useContext } from "react";
import CartContext from "../../store/Cart-context";
import Card from "../UI/Card";
const ProductItem = (props) => {
  const { product} = props;
  
  const cartCtx= useContext(CartContext);
  const addToCartHandler=(qty)=>{
    cartCtx.addItem({
      id:product.id,
      name: product.name,
      qty: qty+1,
      price:product.price
    });
  }
  return (
    <Card>
      <div className="product col">
        <div className="row row-cols-2">
          <div className="col display-img">
            <img src={product.image} className="small-prod-img" alt={product.name} />
            <div className="overlay"></div>
            <button className="btn-cart" onClick={addToCartHandler}>Add to Cart</button>
          </div>
          <div className="col product-detail">
            <h4 className="prod-name">{product.name}</h4>
            <p className="rating">ratings</p>
            <p className="prod-price">Rs {product.price}</p>
          </div>
        </div>
      </div>
    </Card>
  );
};

export default ProductItem;
