import React, { useContext } from "react";
import CartSummary from "./CartSummary";
import ShoppingCart from "./ShoppingCart";
import CartContext from "../../../store/Cart-context";

const Cart = (props) => {
  const { products } = props;
  const cartCtx = useContext(CartContext);
  const hasItems = cartCtx.items.length > 0;
  
  const cartItemRemoveHandler = (id) => {
    cartCtx.removeItem(id);
  };

  const cartItemAddHandler = (item) => {
    cartCtx.addItem({ ...item, amount: 1 });
  };
  return (
    <div class="container">
      <div className="row">
        <div className="col-9"><ShoppingCart products={products} 
        onAdd={cartItemAddHandler}/></div>
        <div className="col-3"><CartSummary /></div>
      </div>
      {hasItems && (<button className="btn-next" type="button">Next</button>)}
      {hasItems &&(<button className="btn-cancel" type="button">Cancel</button>)}
    </div>
    );
};
export default Cart;
