import React, { useContext } from "react";
import CartContext from "../../../store/Cart-context";
import CartItem from "./CartItem";

const ShoppingCart = (props) => {
const cartCtx = useContext(CartContext);
const cartItems=(cartCtx.items.map((items) => (
    <CartItem
      key={items.id}
      name={items.name}
      image={items.image}
      qty={items.qty}
      price={items.price}
    />
  )));
  return (
    <React.Fragment>
      <h4>Shopping Cart</h4>
      <div className="border-small"></div>
      {/* {<div>{props.items.length === 0 && <div>cart is empty.</div>}</div>} */}
      {/* <div className="cart-item">{cartItems}</div> */}
      
    </React.Fragment>
  );
};

export default ShoppingCart;
