import React from "react";
const ShoppingCart = (props) => {
  // const { cartItems, onAdd, onRemove } = props;
  const cartItems = [
    {
      name: "name 2",
      price: 300,
      image:
        "https://i.pinimg.com/originals/62/98/b0/6298b026a65cf80bcf9dce061e9b79c9.png",
      category: "category 2",
      size: "size 2",
      description: "description 2",
      availability: "availability 2",
      brand: "brand 2",
      color: "color 2",
      id: "2",
      qty: 1,
    },
  ].map((items) => (
    <div className="row" key={items.id}>
      <div className="col col-3">{items.image}</div>
      <div className="col col-6">
        <div>{items.name}</div>
        <div>Rs {items.price}</div>
      </div>
      <div className="col">
        <input type="number" step="1" min="1" max="5" value={items.qty}/>
      </div>
    </div>
  ));
  return (
    <React.Fragment>
      <h4>Shopping Cart</h4>
      <div className="border-small"></div>
      {<div>{cartItems.length === 0 && <div>cart is empty.</div>}</div>}
      <div className="row">{cartItems}</div>
    </React.Fragment>
  );
};

export default ShoppingCart;
