const CartItem = ({item}) => {
  
  return (
    <Card>
      <div className="row" key={itemData.id}>
        <div className="col display-img col-3">
          <img src={item.image} className="small-prod-img" alt={item.name} />
        </div>
        <div className="col col-6">
          <div>{itemData.name}</div>
          <div>Rs {item.price}</div>
        </div>
        <div className="col col-3">
          <button type="button" className="btn-addItem">
            +
          </button>
          <input type="number" className="btn addItemBtn" step="1" min="1" max="5" value={items.qty} />
          <button type="button" className="btn removeItemBtn">
            -
          </button>
        </div>
      </div>
    </Card>
  );
};
export default CartItem;
