import Card from "../../UI/Card";
import { connect } from "react-redux";
import {adjustItemQty, removeFromCart} from "../../../Redux/shopping/shopping-actions";

const CartItem = ({ item, adjustItemQty, removeFromCart }) => {
  return (
    <Card>
      <div className="row" key={item.id}>
        <div className="col display-img col-3">
          <img src={item.image} className="small-prod-img" alt={item.name} />
        </div>
        <div className="col col-6">
          <div>{item.name}</div>
          <div>{item.description}</div>
          <div>Rs {item.price}</div>
        </div>
        <div className="col col-3">
          <div>
            <label htmlFor="qty">Qty</label>
            <input
              type="number"
              id="qty"
              step="1"
              min="1"
              max="5"
              value={item.qty}
            />
          </div>
          <button className="removeItem" onClick={()=>removeFromCart(item.id)}>Remove</button>
        </div>
      </div>
    </Card>
  );
};
const mapDispatchToProps = (dispatch) => {
  return {
    adjustQty: (id, value) => dispatch(adjustItemQty(id, value)),
    removeFromCart: (id) => dispatch(removeFromCart(id)),
  };
};

export default connect(null, mapDispatchToProps)(CartItem);
