import Card from "../UI/Card";
import { connect } from "react-redux";
import { addToCart } from "../../Redux/shopping/shopping-actions";

const ProductItem = (product, addToCart) => {
  return (
    <Card>
      <div className="product col">
        <div className="row row-cols-2">
          <div className="col display-img">
            <img src={productData.image} className="small-prod-img" alt={productData.name} />
            <div className="overlay"></div>
            <button className="btn-cart" onClick={()=>addToCart(productData.id)}>Add to Cart</button>
          </div>
          <div className="col product-detail">
            <h4 className="prod-name">{productData.name}</h4>
            <p className="rating">ratings</p>
            <p className="prod-price">Rs {productData.price}</p>
          </div>
        </div>
      </div>
    </Card>
  );
};
const mapDispatchToProps =(dispatch) =>{
  return{
    addToCart:(id) =>dispatch(addToCart(id)),
  };
}

export default connect (null,mapDispatchToProps) (ProductItem);
