import { NavLink } from "react-router-dom";
import HeaderCartButton from "./HeaderCartButton";
import { connect } from "react-redux";
import { useState, useEffect } from "react";

const Header = ({ cart }) => {
  const [cartCount, setCartCount] = useState(0);

  useEffect(() => {
    let count = 0;
    cart.forEach((item) => {count += item.qty;});
    setCartCount(count);
  }, [cart, cartCount]);

  return (

    <nav id="main-menu" class="navbar navbar-expand-lg navbar-light">
  <div class="container-fluid">
    <NavLink to="/home" class="navbar-brand" href="#">E-Shopping</NavLink>
    <button class="navbar-toggler" type="button" data-bs-toggle="collapse" data-bs-target="#navbarNavAltMarkup" aria-controls="navbarNavAltMarkup" aria-expanded="false" aria-label="Toggle navigation">
      <span class="navbar-toggler-icon"></span>
    </button>
    <div class="collapse navbar-collapse" id="navbarNavAltMarkup">
      <div class="navbar-nav">
        <NavLink to="/home" class="nav-link active" aria-current="page" href="#">Home</NavLink>
        <NavLink to="/about" class="nav-link" href="#">About</NavLink>
        <NavLink to="/shop" class="nav-link" href="#">Shop</NavLink>
        <NavLink to="/help" class="nav-link ">Help</NavLink>
        <NavLink to="/cart" class="nav-link "><HeaderCartButton cartCount={cartCount}/></NavLink>
      </div>
    </div>
  </div>
</nav>
  );
};
const mapStateToProps = (state) => {
  return {
    cart: state.shop.cart,
  };
};

export default connect(mapStateToProps)(Header);
