const Cart = (props) => {
  // const { cartItems, onAdd, onRemove } = props;
  const cartItems = [
    {
      name: "name 2",
      price: 300,
      image:
        "https://i.pinimg.com/originals/62/98/b0/6298b026a65cf80bcf9dce061e9b79c9.png",
      category: "category 2",
      size: "size 2",
      description: "description 2",
      availability: "availability 2",
      brand: "brand 2",
      color: "color 2",
      id: "2",
    },
  ];
  return (
    <div className="cart">
      <div>
        {<div>{cartItems.length === 0 && <div>cart is empty.</div>}</div>}
        {cartItems.map((items) => (
          <div className="row" key={items.id}>
            <div>{items.name}</div>
            <div>
              <button className="btn-remove" onClick={() => onRemove(items)}>
                -
              </button>
              <button className="btn-add" onClick={() => onAdd(items)}>
                +
              </button>
            </div>
            <div>
              {items.qty} x {items.price}
            </div>
          </div>
        ))}
      </div>
    </div>
  );
};
export default Cart;
